styles:
:host{
  position:relative;
  display:inline-block; }
  :host path.link{
    fill:none;
    stroke:#666;
    stroke-width:1.5px; }
  :host circle{
    fill:#ccc;
    stroke:#fff;
    stroke-width:1.5px; }
  :host text{
    fill:#000;
    font:10px sans-serif;
    pointer-events:none; }
  :host .legends{
    position:absolute;
    right:20px;
    top:22px;
    z-index:3;
    width:35px;
    font-size:1.2rem;
    background-color:#eee;
    padding:3px 5px;
    border-radius:15px; }
    :host .legends a{
      cursor:pointer;
      font-weight:bold; }
      :host .legends a:hover{
        color:#b65200; }
    :host .legends .info{
      padding:5px;
      border:1px solid #888;
      border-radius:5px;
      position:absolute;
      right:0px;
      font-size:0.7rem;
      line-height:1rem;
      -webkit-box-shadow:1px 1px 3px #bbb;
              box-shadow:1px 1px 3px #bbb;
      background-color:#fff;
      width:350px; }
      :host .legends .info strong{
        color:#8f0000;
        font-size:0.8rem;
        margin-left:20px; }
  :host #d3-container{
    border:1px solid #633;
    padding:0 5px;
    -webkit-box-sizing:border-box;
            box-sizing:border-box;
    border-radius:5px;
    background-color:#fefefe;
    margin:5px;
    overflow:hidden; }
